package com.example.userssp

import android.content.Context
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.util.Log
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.userssp.databinding.ActivityMainBinding
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.textfield.TextInputEditText

class MainActivity : AppCompatActivity(), OnClickListener {

    private lateinit var userAdapter: UserAdapter
    private lateinit var linearLayoutManager: RecyclerView.LayoutManager
    private lateinit var binding: ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        val preferences = getPreferences(Context.MODE_PRIVATE)

        val isFirstTime = preferences.getBoolean(getString(R.string.sp_first_time), true)
        Log.i("SP", "${getString(R.string.sp_first_time)} = $isFirstTime")

        if (isFirstTime) {

            val dialogView = layoutInflater.inflate(R.layout.dialog_register, null)

            MaterialAlertDialogBuilder(this)
                .setTitle(R.string.dialog_title)
                .setView(dialogView)
                .setCancelable(false)
                .setPositiveButton(R.string.dialog_confirm) { _, _ ->
                    val username =
                        dialogView.findViewById<TextInputEditText>(R.id.etUsername).text.toString()
                    with(preferences.edit()) {
                        putBoolean(getString(R.string.sp_first_time), false)
                        putString(getString(R.string.sp_username), username)
                            .apply()
                    }
                    Toast.makeText(this, R.string.register_success, Toast.LENGTH_SHORT).show()
                }
                .show()
        } else {
            val username = preferences.getString(getString(R.string.sp_username), getString(R.string.hint_username))
            Toast.makeText(this, "Bienvenido $username", Toast.LENGTH_SHORT).show()

        }
        userAdapter = UserAdapter(getUsers(), this)
        linearLayoutManager = LinearLayoutManager(this)

        binding.recyclerView.apply {
            setHasFixedSize(true)
            layoutManager = linearLayoutManager
            adapter = userAdapter
        }
    }

    private fun getUsers(): MutableList<User> {
        val users = mutableListOf<User>()

        val sebas = User(
            1,
            "Sebastian",
            "Gonzalez",
            "data:image/jpeg;base64,"
        )
        val milsim = User(
            2,
            "Milsim",
            "Scarface",
            "https://i.pinimg.com/236x/ed/cd/ba/edcdbab8be8b4527cf83ac26f64d69fe.jpg"
        )
        val rush = User(
            3,
            "Rusher",
            "Man",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQgAQCR3ZStfEuI1WMVN3gtr-MNMnwJ2syDYg&usqp=CAU"
        )
        val Yio = User(
            4,
            "Yio",
            "YT",
            "data:image/jpeg;base64,"
        )


        users.add(sebas)
        users.add(milsim)
        users.add(rush)
        users.add(Yio)
        users.add(sebas)
        users.add(milsim)
        users.add(rush)
        users.add(Yio)
        users.add(sebas)
        users.add(milsim)
        users.add(rush)
        users.add(Yio)
        users.add(sebas)
        users.add(milsim)
        users.add(rush)
        users.add(Yio)
        users.add(sebas)
        users.add(milsim)
        users.add(rush)
        users.add(Yio)

        return users
    }

    override fun onClick(user: User, position: Int) {
        Toast.makeText(this, "$position: ${user.getFullName()}", Toast.LENGTH_SHORT).show()
    }
}